import React from "react"
import { HashLink as Link } from "react-router-hash-link"
import Love from "./Love";
import Contact from "./Contact";
import Stuff from "./Stuff";



const Categoriesbuttons = () => {
    const [currentContent, setCurrentContent] = useState('home');

  const renderContent = () => {
    switch (currentContent) {
      case 'home':
        return <Love />;
      case 'about':
        return <Contact />;
      case 'contact':
        return <ContactContent />;
      default:
        return null;
    }
  };
    return (
            <div className="w-full px-40 pt-10" >
                <ul className="flex flex-row flex-wrap justify-between align-middle w-full">
                    <button>F</button>
                    <li><Link to="#stuff">Mystery and Crime</Link></li>
                    <li><Link to="#contact">Science and Nature</Link></li>
                    <li><Link to="#contact">Romance</Link></li>
                    <li><Link to="#contact">History</Link></li>
                    <li><Link to="#contact">Business</Link></li>
                </ul>
                {/* <div className="content">
                   <Love/>
                    <Stuff />
                    <Contact/>
                </div> */}
            </div>
    )
}


export default Categoriesbuttons;



