import React, { useEffect, useState } from "react";
import Book from "../BookList/Book";
import coverImg from "../../Images/cover_not_found.jpg"


const RomanceBooksComponent = () => {
  const [catBooks, setCatBooks] = useState([]);
  const [booksWithCovers, setBooksWithCovers] = useState([]);

  async function getRomanceBooks() {
    try {
      const res = await fetch("http://openlibrary.org/subjects/science.json");
      console.log(res)
      const data = await res.json();
      setCatBooks(data);
      console.log(data, 'data');
    } catch (error) {
      console.log(error, 'error')
    }

    const booksWithCoversData = catBooks.map((singleBook) => {
      return {
        ...singleBook,
        id: (singleBook.id).replace("/works/", ""),
        cover_img: singleBook.cover_id ? `https://covers.openlibrary.org/b/id/${singleBook.cover_id}-L.jpg` : coverImg,
      }
    })

    setBooksWithCovers(booksWithCoversData);

  }

  useEffect(() => {
    getRomanceBooks();
  })
  return (
    <section className='booklist'>
      {booksWithCovers && (
        <div className='container'>
          {/* <div className='gap-12 grid'>
            {
              booksWithCovers.slice(0, 30).map((item, index) => {
                return (
                  <Book key={index} {...item} />
                )
              })
            }
          </div> */}
        </div>
      )}
    </section>
  );
}


export default RomanceBooksComponent;
